var axisObj = {};
var rootObj = this;


// Function to Swiper slider
axisObj.slider = function(obj, opt) {
    rootObj[obj] = new Swiper($(obj).find('.swiper-container'), opt);
}

function set_bg() {
    $('.cm-set-bg').each(function() {
        $(this).css('background-image', 'url(' + $(this).attr('data-img') + ')');
        $(this).css('background-image', 'cover');
    });
}

$(function() {
    $("a").each(function() {
        if ($(this).attr('href') == '#' || $(this).attr('href') == ' ') {
            $(this).attr('href', 'javascript:void(0)');
        }
    });

    if($(".cm-set-bg").length != 0){
        set_bg();
    }


    $(document).on('click', '#stock-play-btn', function () {
       
        setTimeout(function(){
            $("#stock-play-btn").fadeOut('normal');
            $(".video-thumbnail").fadeOut('normal');
            $(".video-title").fadeOut('normal');
            console.log("play");
             player1.playVideo();
        }, 800);
    });

    var slideSpace;
    if($(window).width < 769){
        slideSpace = -0.5;
    }
    else{
        slideSpace = -0.6;
    }
    // Function to illustration for mobile
    if ($(".js-banner").length != 0) {
        var opt = {
            autoplay: {
                delay: 3000,
              },
              // init: false,
              initialSlide:0,
              loop: true,
              speed:800,
              slidesPerView: 5, // or 'auto'
              spaceBetween: 20,
              centeredSlides : true,
              slideToClickedSlide:true,
              effect: 'coverflow', // 'cube', 'fade', 'coverflow',
              coverflowEffect: {
                rotate: 20, // Slide rotate in degrees
                stretch: 0, // Stretch space between slides (in px)
                depth: 100, // Depth offset in px (slides translate in Z axis)
                modifier: 1, // Effect multipler
                slideShadows : true, // Enables slides shadows
              },
              grabCursor: false,
              breakpoints: {
                768: {
                    // slidesPerView: 5, // or 'auto'
                    spaceBetween: 20,
                    centeredSlides : true,
                    effect: 'coverflow', // 'cube', 'fade', 'coverflow',
                    coverflowEffect: {
                        rotate: 50, // Slide rotate in degrees
                        stretch: 0, // Stretch space between slides (in px)
                        depth: 100, // Depth offset in px (slides translate in Z axis)
                        modifier: 1, // Effect multipler
                        slideShadows : true, // Enables slides shadows
                    },
                  slidesPerView: 2,
                  spaceBetween:0,
                  pagination: {
                    el: '.swiper-pagination',
                    clickable: true,
                  },
                }
              },
        }
        axisObj.slider('.js-banner', opt)
    }

})


